const target = 10;

function findTwoNumbers(arr, target) {
    const numsMap = new Map()

    for(let i = 0; i < arr.length; i++){
        const cur = arr[i]

        if(numsMap.has(target - cur)){
            return [cur, target - cur]
        } else {
            numsMap.set(cur, i);
        }
    }

    return []
};

