const getAveragePositive = (arr) => {
  let res = 0
  let count = 0

  for (let num of arr) {
    if (num >= 0) {
      res += num
      count++
    }
  }

  if (count === 0) {
    return 0
  }

  let avarage = res / count

  return avarage
};

console.log(getAveragePositive([])); // 0 || NaN
