```function isBalance(string) {
  let stack = []
  const brackets = {
    "}": "{",
    ")": "(",
    "]": "[",
  }
  for(let char of string) {
    if(["{", "[", "("].includes(char)) {
      stack.push(char)
    } else if(["}", "]", ")"].includes(char)) {
      if(stack.pop() !== brackets[char]) return false
    }
  }
  return stack.length === 0;
}```

