```function isValid(str) {
  const stack = [];
  const brackets = {
    '(': ')',
    '{': '}',
    '[': ']'
  };

  for (let char of str) {
    if (brackets[char]) {
      // –µ—Å–ª–∏ –æ—Ç–∫—Ä—ã–≤–∞—é—â–∞—è —Å–∫–æ–±–∫–∞, –¥–æ–±–∞–≤–ª—è–µ–º –≤ —Å—Ç–µ–∫
      stack.push(char);
    } else if (Object.values(brackets).includes(char)) {
      // –µ—Å–ª–∏ –∑–∞–∫—Ä—ã–≤–∞—é—â–∞—è —Å–∫–æ–±–∫–∞, –ø—Ä–æ–≤–µ—Ä—è–µ–º —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–µ
      if (brackets[stack.pop()] !== char) {
