import {useEffect, useState, useCallback} from "react";

export default function PokemonsNamesList({limit = 10}) {
    const url = `https://pokeapi.co/api/v2/pokemon?limit=${limit}&offset=0`;
    const [activePokemonName, setActivePokemonName] = useState('')
    const [pokemonsNames, setPokemonsNames] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);


    const fetchPokemons = async () => {
        try {
            setErrorMessage(null)
            const res = await fetch(url)
            const data = await res.json()
            setPokemonsNames(data.results)

            if (!res.ok) {
                throw new Error('–û—à–∏–±–∫–∞')
            }

        } catch (e) {
            setErrorMessage(e.message)
        }

    }

    useEffect(() => {
        fetchPokemons()
    }, [limit]);


    return (
        <ul>
            {pokemonsNames.map((pokemon) => {
            const {name} = pokemon
                return (
                    <li
                        key={name}
                        onClick={() => setActivePokemonName(name)}
                        className={activePokemonName === name ? 'item--active' : 'item'}
                    >
                        {name}
                    </li>
                )
            })}
        </ul>
    );
}```

