    let currentLevel = result;

    // –ü–µ—Ä–µ–±–∏—Ä–∞–µ–º –∫–ª—é—á–∏
    for (let i = 0; i < keys.length; i++) {
        // –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –æ–±—ä–µ–∫—Ç –¥–ª—è —Ç–µ–∫—É—â–µ–≥–æ –∫–ª—é—á–∞
        currentLevel[keys[i]] = {};

        // –ü–µ—Ä–µ–º–µ—â–∞–µ–º —Ç–µ–∫—É—â–∏–π —É—Ä–æ–≤–µ–Ω—å –Ω–∞ –Ω–æ–≤—ã–π —Å–æ–∑–¥–∞–Ω–Ω—ã–π –æ–±—ä–µ–∫—Ç
        currentLevel = currentLevel[keys[i]];
    }

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∏—Ç–æ–≥–æ–≤—ã–π –≤–ª–æ–∂–µ–Ω–Ω—ã–π –æ–±—ä–µ–∫—Ç
    return result;
