        }
    }

    // –ü–µ—Ä–µ–±–∏—Ä–∞–µ–º –º–∞—Å—Å–∏–≤, –Ω–∞—á–∏–Ω–∞—è —Å–æ –≤—Ç–æ—Ä–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
    for (let i = 1; i < list.length; i++) {
        if (list[i] === end + 1) {
            // –ï—Å–ª–∏ —Ç–µ–∫—É—â–∏–π —ç–ª–µ–º–µ–Ω—Ç —Å–ª–µ–¥—É–µ—Ç –∑–∞ –ø—Ä–µ–¥—ã–¥—É—â–∏–º, –æ–±–Ω–æ–≤–ª—è–µ–º –∫–æ–Ω–µ—Ü –¥–∏–∞–ø–∞–∑–æ–Ω–∞
            end = list[i];
        } else {
            // –ï—Å–ª–∏ —Ç–µ–∫—É—â–∏–π —ç–ª–µ–º–µ–Ω—Ç –Ω–µ —Å–ª–µ–¥—É–µ—Ç –∑–∞ –ø—Ä–µ–¥—ã–¥—É—â–∏–º, –¥–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—É—â–∏–π –¥–∏–∞–ø–∞–∑–æ–Ω –≤ –º–∞—Å—Å–∏–≤ –¥–∏–∞–ø–∞–∑–æ–Ω–æ–≤
            addRange();
            // –ù–∞—á–∏–Ω–∞–µ–º –Ω–æ–≤—ã–π –¥–∏–∞–ø–∞–∑–æ–Ω
            start = list[i];
            end = list[i];
        }
    }

    // –î–æ–±–∞–≤–ª—è–µ–º –ø–æ—Å–ª–µ–¥–Ω–∏–π –¥–∏–∞–ø–∞–∑–æ–Ω
    addRange();

