```function range(start, stop, step) {
    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –∑–Ω–∞—á–µ–Ω–∏—è –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é –¥–ª—è start, stop –∏ step
    if (arguments.length === 1) {
        // –ï—Å–ª–∏ –ø–µ—Ä–µ–¥–∞–Ω —Ç–æ–ª—å–∫–æ –æ–¥–∏–Ω –∞—Ä–≥—É–º–µ–Ω—Ç, –æ–Ω —Å—á–∏—Ç–∞–µ—Ç—Å—è stop, start = 0, step = 1
        stop = start;
        start = 0;
        step = 1;
    } else if (arguments.length === 2) {
        // –ï—Å–ª–∏ –ø–µ—Ä–µ–¥–∞–Ω–æ –¥–≤–∞ –∞—Ä–≥—É–º–µ–Ω—Ç–∞, –æ–Ω–∏ —Å—á–∏—Ç–∞—é—Ç—Å—è start –∏ stop, step = 1
        step = 1;
    }

    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º –º–∞—Å—Å–∏–≤ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
    let result = [];

    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∏—Ç–µ—Ä–∞—Ü–∏–∏
    if (step > 0) {
        // –ï—Å–ª–∏ step –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω—ã–π, –∏—Ç–µ—Ä–∏—Ä—É–µ–º –æ—Ç start –¥–æ stop —Å —à–∞–≥–æ–º step
        for (let i = start; i < stop; i += step) {
            result.push(i);
        }
    } else {
        // –ï—Å–ª–∏ step –æ—Ç—Ä–∏—Ü–∞—Ç–µ–ª—å–Ω—ã–π, –∏—Ç–µ—Ä–∏—Ä—É–µ–º –æ—Ç start –¥–æ stop —Å —à–∞–≥–æ–º step
        for (let i = start; i > stop; i += step) {
            result.push(i);
        }
    }

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –º–∞—Å—Å–∏–≤ —Å —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–º
    return result;
}

