```function processOrder(store, order) {
    // –°–æ–∑–¥–∞–µ–º –æ–±—ä–µ–∫—Ç –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞ –∫ –∫–æ–ª–∏—á–µ—Å—Ç–≤—É –æ–¥–µ–∂–¥—ã –ø–æ —Ä–∞–∑–º–µ—Ä—É
    const storeMap = new Map();
    for (const item of store) {
        storeMap.set(item.size, item.quantity);
    }

    // –°–æ—Ä—Ç–∏—Ä—É–µ–º –∑–∞–∫–∞–∑—ã, —á—Ç–æ–±—ã —Å–Ω–∞—á–∞–ª–∞ –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞—Ç—å —Ç–µ, —É –∫–æ—Ç–æ—Ä—ã—Ö –º–µ–Ω—å—à–µ –≤–∞—Ä–∏–∞–Ω—Ç–æ–≤ —Ä–∞–∑–º–µ—Ä–æ–≤
    const sortedOrder = [...order].sort((a, b) => a.size.length - b.size.length);

    const assignments = [];

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–æ–≤
    function assignSizes(index, currentStore, currentAssignments) {
        // –ï—Å–ª–∏ –≤—Å–µ –∑–∞–∫–∞–∑—ã –æ–±—Ä–∞–±–æ—Ç–∞–Ω—ã, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Ç–µ–∫—É—â–∏–µ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏—è
        if (index === sortedOrder.length) {
            return currentAssignments;
        }

        const orderItem = sortedOrder[index];
        const possibleSizes = orderItem.size;

        for (const size of possibleSizes) {
            if (currentStore.get(size) && currentStore.get(size) > 0) {
                // –ù–∞–∑–Ω–∞—á–∞–µ–º —Ç–µ–∫—É—â–∏–π —Ä–∞–∑–º–µ—Ä
                currentStore.set(size, currentStore.get(size) - 1);
                currentAssignments.push({ id: orderItem.id, size: size });

                // –†–µ–∫—É—Ä—Å–∏–≤–Ω–æ –Ω–∞–∑–Ω–∞—á–∞–µ–º —Å–ª–µ–¥—É—é—â–∏–π –∑–∞–∫–∞–∑
                const result = assignSizes(index + 1, currentStore, currentAssignments);
                if (result) {
                    return result;
                }

                // –û—Ç–∫–∞—Ç—ã–≤–∞–µ–º –∏–∑–º–µ–Ω–µ–Ω–∏—è, –µ—Å–ª–∏ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏–µ –Ω–µ —É–¥–∞–ª–æ—Å—å
                currentStore.set(size, currentStore.get(size) + 1);
                currentAssignments.pop();
            }
        }

        // –ï—Å–ª–∏ –Ω–∏ –æ–¥–∏–Ω –∏–∑ —Ä–∞–∑–º–µ—Ä–æ–≤ –Ω–µ –ø–æ–¥—Ö–æ–¥–∏—Ç, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º null
        return null;
    }

    // –ù–∞—á–∏–Ω–∞–µ–º —Ä–µ–∫—É—Ä—Å–∏–≤–Ω–æ–µ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏–µ —Å –ø–µ—Ä–≤–æ–≥–æ –∑–∞–∫–∞–∑–∞
    const finalAssignments = assignSizes(0, new Map(storeMap), []);

    if (!finalAssignments) {
        return false;
    }

    // –°–æ–∑–¥–∞–µ–º —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É –Ω–∞ –±–∞–∑–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–Ω—ã—Ö —Ä–∞–∑–º–µ—Ä–æ–≤
    const statsMap = new Map();
    for (const assignment of finalAssignments) {
        statsMap.set(assignment.size, (statsMap.get(assignment.size) || 0) + 1);
    }

    const stats = [];
    for (const [size, count] of statsMap.entries()) {
        stats.push({ size: size, quantity: count });
    }

    // –°–æ—Ä—Ç–∏—Ä—É–µ–º —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É –ø–æ –≤–æ–∑—Ä–∞—Å—Ç–∞–Ω–∏—é —Ä–∞–∑–º–µ—Ä–æ–≤
    stats.sort((a, b) => a.size - b.size);

    return { stats, assignment: finalAssignments };
}```
