const AppTimer = () => {
  const [timer, setTimer] = useState(10)
  const isTimer = useRef(false)
  useEffect(() => {
    const intervalId = setInterval(() => {
      if (!isTimer.current) {
        setTimer((prev) => {
          if (prev === 1) {
            clearInterval(intervalId)
          }
          return prev - 1
        })
      }
    }, 1000)
    return () => clearInterval(intervalId)
  }, [])
  return (
    <div>
      {' '}
      <div className="Controls">{timer}</div> <ProductList />{' '}
    </div>
  )
}

