    const fetchProducts = async () => {
      const productsResponse = await fetch('https://my-json-server.typicode.com/cyberwalrus/demo/products')
      const productsJson = await productsResponse.json()
      setProducts(productsJson)
    }
    fetchProducts()
  }, [])
