function camelToSnake(text){
    let result = []
    for (let i=0; i < text.length; i++){
        if (i === 0) {
            result.push(text[0].toLowerCase())
        } else {
            if (text[i] === text[i].toUpperCase()) {
                result.push(`_${text[i].toLowerCase()}`)
            }   else {
                result.push(text[i])
            }
        }
    }
    return result.join('')
}


