```export const ContentHolder = <OptionValue extends PossibleOptionValue>(
  props: ContentHolderProps<OptionValue>
) => {
  const { getOptionMetadata, onDelete, value } = props;
  const [overflowIndex, setOverflowIndex] = useState<undefined | number>();
  const spanRef = useRef<HTMLSpanElement>(null);
  const extraChipRef = useRef<HTMLDivElement>(null);
  const renderedWidthRef = useRef<number[]>([]);

  if (value.length === 0) {
    return null;
  }

  const renderedValue = useMemo(() => {
    setOverflowIndex(undefined);
    renderedWidthRef.current = [];

    return value.map((v) => {
      const option = getOptionMetadata(v);
      if (option) {
        renderedWidthRef.current.push(
          getTextWidth(option.label as string, ChipFont) + chipSpacing
        );
      }

      return (
        value && (
          <SelectChip
            size="small"
            label={option.label}
            key={value}
            onDelete={(event) => onDelete(event, option.value)}
          />
        )
      );
    });
  }, [value, getOptionMetadata, onDelete]);

  const renderedWithoutOverflow = useMemo(
    () =>
      overflowIndex
        ? renderedValue.slice(0, overflowIndex || 1)
        : renderedValue,
    [renderedValue, overflowIndex]
  );

  useLayoutEffect(() => {
    const wrapperElement = spanRef.current;

    if (!(value.length > 1 && wrapperElement)) {
      return;
    }

    const wrapperClientRect = wrapperElement.getBoundingClientRect();
    const renderedWidth = renderedWidthRef.current;
    const [occupiedWidth] = renderedWidth;
    let freeWidth = wrapperClientRect.width - occupiedWidth;

    if (freeWidth > 0) {
      let index: undefined | number;
      for (let i = 1; i < value.length; i = i + 1) {
        const elementWidth = renderedWidth[i];
        const occupiedElementWidth = 4 + elementWidth;
        if (freeWidth < occupiedElementWidth) {
          const restCount = value.length - i || 1;

          const occupiedRestCountWidth =
            4 + getTextWidth(`+${restCount}`, ChipFont) + chipSpacing;
          if (freeWidth >= occupiedRestCountWidth) {
            index = i;
          } else {
            index = i - 1;
          }
          break;
        }

        freeWidth = freeWidth - occupiedElementWidth;
      }

      setOverflowIndex(index);
    } else {
      setOverflowIndex(1);
    }
  }, [value]);

  return (
    <SpanContentHolder ref={spanRef}>
      {renderedWithoutOverflow}
      {Number.isFinite(overflowIndex) && (
        <SelectChip
          size="small"
          label={`+${value.length - overflowIndex!}`}
          ref={extraChipRef}
          sx={{ flexShrink: 0 }}
        />
      )}
    </SpanContentHolder>
  );
};

export const MultipleSelectToggleField = React.forwardRef(
  (
