  removeEventListener(event, listener) {
    const listeners = this.listeners[event]

    if (!listeners) return

    this.listeners[event] = listeners.filter((l) => l !== listener)
  }

 once(event, listener) {
    const executeOnceWrapper = () => {
      listener()
      this.removeEventListener(event, executeOnceWrapper)
    }

    return this.addEventListener(event, executeOnceWrapper);
  }

  dispatchEvent(event, payload, actionQueue) {
    const listeners = this.listeners[event]

    if (!listeners) return

    const microTaskPromise = new Promise((resolve, reject) => {
      resolve()
    })

    listeners.forEach((listener) => {
      switch (actionQueue) {
        case "micro":
          microTaskPromise.then(() => {
            listener(payload)
          })
          break
        case "macro":
          setTimeout(() => {
            listener(payload)
          }, 0)
          break
        default:
          listener(payload);
          break;
      }
    })
  }
}

