```const nominals = [10, 50, 100, 200, 500, 1000, 2000, 5000];

function atm(amount, nominals) {
  // –†–µ–∑—É–ª—å—Ç–∏—Ä—É—é—â–∏–π –º–∞—Å—Å–∏–≤ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Å—Ç—Ä–æ–∫ —Ñ–æ—Ä–º–∞—Ç–∞ "–Ω–æ–º–∏–Ω–∞–ª x –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ"
  const result = [];

  // –ü–µ—Ä–µ–±–∏—Ä–∞–µ–º –Ω–æ–º–∏–Ω–∞–ª—ã –≤ –æ–±—Ä–∞—Ç–Ω–æ–º –ø–æ—Ä—è–¥–∫–µ (–æ—Ç –±–æ–ª—å—à–µ–≥–æ –∫ –º–µ–Ω—å—à–µ–º—É)
  for (let i = nominals.length - 1; i >= 0; i--) {
    const nominal = nominals[i];

    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫—É–ø—é—Ä —Ç–µ–∫—É—â–µ–≥–æ –Ω–æ–º–∏–Ω–∞–ª–∞
    const count = Math.floor(amount / nominal);

    // –ï—Å–ª–∏ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –±–æ–ª—å—à–µ –Ω—É–ª—è, –¥–æ–±–∞–≤–ª—è–µ–º —Å—Ç—Ä–æ–∫—É –≤ —Ä–µ–∑—É–ª—å—Ç–∞—Ç
    if (count > 0) {
      result.push(`${nominal}x${count}`);
      amount -= nominal * count; // –£–º–µ–Ω—å—à–∞–µ–º —Å—É–º–º—É –Ω–∞ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–Ω—ã–µ –∫—É–ø—é—Ä—ã
    }
  }

  return result;
}

