Array.prototype.groupBy = function(callback) {
  return this.reduce((acc, item) => {
    let key = [callback(item)]
    if(!acc[key]) {
      acc[key] = [item]
    } else {
      acc[key].push(item)
    }
    return acc
  }, {})
}

