function sortOdds(arr) {
  // –ò–∑–≤–ª–µ–∫–∞–µ–º –≤—Å–µ –Ω–µ—á–µ—Ç–Ω—ã–µ —á–∏—Å–ª–∞ –∏–∑ –º–∞—Å—Å–∏–≤–∞
  const odds = arr.filter(num => num % 2 !== 0);

  // –°–æ—Ä—Ç–∏—Ä—É–µ–º –Ω–µ—á–µ—Ç–Ω—ã–µ —á–∏—Å–ª–∞ –ø–æ –≤–æ–∑—Ä–∞—Å—Ç–∞–Ω–∏—é
  odds.sort((a, b) => a - b);

  // –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –º–∞—Å—Å–∏–≤, –∑–∞–º–µ–Ω—è—è –Ω–µ—á–µ—Ç–Ω—ã–µ —á–∏—Å–ª–∞ –æ—Ç—Å–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–Ω—ã–º–∏
  return arr.map(num => (num % 2 !== 0 ? odds.shift() : num));
}```

