// IN: AAAABBBCCXYZDDDDEEEFFFAAAAAABBBBBBBBBBBBBBBBBBBBBBBBBBBB
// OUT: A4B3C2XYZD4E3F3A6B28

function zipStr(str) {

    let result = ''

    let count = 1

    for(let i = 0; i < str.length; i++) {
        if(str[i] === str[i+1]){
            count++
        }else{
            result += count > 1 ? str[i]+count : str[i]
            count = 1
        }
    }
    return result

};


