    const currentFormatParts = currentFormat.split(/[^a-zA-Z]/);
    const dateParts = dateStr.split(/\D/);

    // –°–æ–∑–¥–∞–µ–º –æ–±—ä–µ–∫—Ç –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –∑–Ω–∞—á–µ–Ω–∏–π –¥–Ω—è, –º–µ—Å—è—Ü–∞ –∏ –≥–æ–¥–∞
    const dateValues = {};
    currentFormatParts.forEach((part, index) => {
        dateValues[part] = dateParts[index];
    });

    // –†–∞–∑–¥–µ–ª—è–µ–º —Ü–µ–ª–µ–≤–æ–π —Ñ–æ—Ä–º–∞—Ç
    const targetFormatParts = targetFormat.split(/[^a-zA-Z]/);

    // –§–æ—Ä–º–∏—Ä—É–µ–º –Ω–æ–≤—É—é —Å—Ç—Ä–æ–∫—É –¥–∞—Ç—ã –≤ —Ü–µ–ª–µ–≤–æ–º —Ñ–æ—Ä–º–∞—Ç–µ
    const newDateParts = targetFormatParts.map(part => dateValues[part]);

    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –Ω–æ–≤—ã–π —Ä–∞–∑–¥–µ–ª–∏—Ç–µ–ª—å –∏–∑ —Ü–µ–ª–µ–≤–æ–≥–æ —Ñ–æ—Ä–º–∞—Ç–∞
    const newSeparator = targetFormat.match(/[^a-zA-Z]/)[0];

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –Ω–æ–≤—É—é —Å—Ç—Ä–æ–∫—É –¥–∞—Ç—ã
    return newDateParts.join(newSeparator);
}

