```function printStructure(root) {
  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º —Å—Ç–µ–∫ —Å –∫–æ—Ä–Ω–µ–≤—ã–º —É–∑–ª–æ–º –∏ –≥–ª—É–±–∏–Ω–æ–π 0
  const stack = [{ node: root, depth: 0 }];

  // –ü–æ–∫–∞ —Å—Ç–µ–∫ –Ω–µ –ø—É—Å—Ç, –ø—Ä–æ–¥–æ–ª–∂–∞–µ–º –æ–±—Ö–æ–¥
  while (stack.length > 0) {
    // –ò–∑–≤–ª–µ–∫–∞–µ–º —Ç–µ–∫—É—â–∏–π —É–∑–µ–ª –∏ –µ–≥–æ –≥–ª—É–±–∏–Ω—É –∏–∑ —Å—Ç–µ–∫–∞
    const { node, depth } = stack.pop();

    // –í—ã–≤–æ–¥–∏–º –∏–º—è —É–∑–ª–∞ —Å –æ—Ç—Å—Ç—É–ø–∞–º–∏, —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–º–∏ –µ–≥–æ –≥–ª—É–±–∏–Ω–µ
    console.log('  '.repeat(depth) + node.name);

    // –ï—Å–ª–∏ —É —É–∑–ª–∞ –µ—Å—Ç—å –¥–µ—Ç–∏, –¥–æ–±–∞–≤–ª—è–µ–º –∏—Ö –≤ —Å—Ç–µ–∫
    if (node.children) {
      // –î–æ–±–∞–≤–ª—è–µ–º –¥–µ—Ç–µ–π –≤ —Å—Ç–µ–∫ –≤ –æ–±—Ä–∞—Ç–Ω–æ–º –ø–æ—Ä—è–¥–∫–µ, —á—Ç–æ–±—ã –æ–Ω–∏ –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞–ª–∏—Å—å –≤ –ø—Ä–∞–≤–∏–ª—å–Ω–æ–º –ø–æ—Ä—è–¥–∫–µ
      for (let i = node.children.length - 1; i >= 0; i--) {
        stack.push({ node: node.children[i], depth: depth + 1 });
      }
    }
  }
}```

