```import { useContext } from 'react';
import { UserContext } from './api/FetchUser';
import { UserRole } from './user';

type ButtonConfig = {
  label: string;
  role: UserRole[];
  requiresSubscription?: boolean;
};

export const ButtonsUser = () => {
  const user = useContext(UserContext);

  if (!user) {
    return <div>Loading...</div>;
  }

  const buttons: ButtonConfig[] = [
    { label: 'Public', role: [] },
    { label: 'Protected', role: ['user'] },
    {
      label: 'Protected Subscription',
      role: ['user'],
      requiresSubscription: true,
    },
    { label: 'Protected Admin', role: ['admin'] },
  ];

  return (
    <div>
      {buttons.map((button) => {
        const hasRole =
          button.role.length === 0 ||
          button.role.some((r) => user.roles.includes(r));
        const hasSubscription =
          !button.requiresSubscription || user.hasSubscription;

        if (hasRole && hasSubscription) {
          return <button key={button.label}>{button.label}</button>;
        }
        return null;
      })}
      <div>User: {user.name}</div>
      <div>Roles: {user.roles.join(', ')}</div>
    </div>
  );
};```

