```function readMessage(input) {
    let messageSize = 12; // –†–∞–∑–º–µ—Ä –∫–∞–∂–¥–æ–≥–æ —Å–æ–æ–±—â–µ–Ω–∏—è
    let messages = []; // –ú–∞—Å—Å–∏–≤ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Å–æ–æ–±—â–µ–Ω–∏–π

    for (let i = 0; i < input.length; i += messageSize) {
        // –ò–∑–≤–ª–µ–∫–∞–µ–º –ø–æ–¥–º–∞—Å—Å–∏–≤ —Ä–∞–∑–º–µ—Ä–æ–º messageSize
        let message = input.slice(i, i + messageSize);

        if (message.length === messageSize) { // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –¥–ª–∏–Ω–∞ —Å–æ–æ–±—â–µ–Ω–∏—è —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É–µ—Ç size
            messages.push(message); // –î–æ–±–∞–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ –º–∞—Å—Å–∏–≤
        }
    }

    return messages; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –º–∞—Å—Å–∏–≤ —Å–æ–æ–±—â–µ–Ω–∏–π
}

