const calculateRoute = (tickets) => {
  const fromMap = new Map();
  const toMap = new Map();

  tickets.forEach((ticket) => {
    fromMap.set(ticket.from, ticket.to);

    toMap.set(ticket.to, ticket.from);
  });

  let start = null;
  for (let [from] of fromMap) {
    if (!toMap.has(from)) {
      start = from;
      break;
    }
  }

  const route = [];

  while (start) {
    const destination = fromMap.get(start);

    if (destination) {
      route.push({ from: start, to: destination });
    }

    start = destination;
    // route.push(start);
    // start = fromMap.get(start);
  }

  return route;
};

