// # –≤—ã–≤–æ–¥
// champions = { userIds: [1, 2], steps: 5500 }

function findChampions(statistics) {
    const userSteps = new Map();
    const daysCount = statistics.lenght;

    let maxSteps = 0;
    const champions = [];

    statistics.forEach((dayStats, i) => {
        dayStats.forEach(({userId, steps}) => {
            if (i === 0) {
                userSteps.set(userId, {
                    totalSteps: steps,
                    days: 1,
                    });
            } else {
                const currentUser = userSteps.get(userId);
                if (currentUser) {
                    userSteps.set(userId, {
                    totalSteps: currentUser.totalSteps + steps,
                    days: currentUser.days + 1,
                    })
                }
            }
        })
    })

    userSteps.forEach((userId, {totalSteps, days}) => {
        if (days === daysCount) {
            if (totalSteps > maxSteps) {
                maxSteps = totalSteps;
                champions.length = 0;
                champions.push(userId);
            } else if (totalSteps === maxSteps) {
                champions.push(userId);
            }
        }
    })

    return {
        userIds: champions,
        steps: maxSteps,
    };
}```

