```function groupAnagrams(strs) {
  const map = {}; // –û–±—ä–µ–∫—Ç –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –≥—Ä—É–ø–ø –∞–Ω–∞–≥—Ä–∞–º–º

  strs.forEach(str => {
    // –°–æ—Ä—Ç–∏—Ä—É–µ–º –±—É–∫–≤—ã –≤ —Å–ª–æ–≤–µ –ø–æ –∞–ª—Ñ–∞–≤–∏—Ç—É, —ç—Ç–æ –±—É–¥–µ—Ç –∫–ª—é—á
    const sortedStr = str.split('').sort().join('');

    // –ï—Å–ª–∏ –∫–ª—é—á —É–∂–µ –µ—Å—Ç—å –≤ –æ–±—ä–µ–∫—Ç–µ, –¥–æ–±–∞–≤–ª—è–µ–º —Ç—É–¥–∞ —Å–ª–æ–≤–æ
    if (map[sortedStr]) {
      map[sortedStr].push(str);
    } else {
      // –ï—Å–ª–∏ –∫–ª—é—á–∞ –Ω–µ—Ç, —Å–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –º–∞—Å—Å–∏–≤ —Å —ç—Ç–∏–º —Å–ª–æ–≤–æ–º
      map[sortedStr] = [str];
