  });

  // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –≥—Ä—É–ø–ø—ã –∞–Ω–∞–≥—Ä–∞–º–º –∫–∞–∫ –º–∞—Å—Å–∏–≤ –º–∞—Å—Å–∏–≤–æ–≤
  return Object.values(map);
}

