```function fetchWithRetry(url, attempts) {
  return new Promise((resolve, reject) => {
    const attemptFetch = (n) => {
      fetch(url)
        .then((response) => {
          if (!response.ok) {
            // –ï—Å–ª–∏ –æ—Ç–≤–µ—Ç –Ω–µ —É—Å–ø–µ—à–µ–Ω, —Å–æ–∑–¥–∞–µ–º –æ—à–∏–±–∫—É
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return resolve(response); // –ï—Å–ª–∏ —É—Å–ø–µ—à–µ–Ω, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –æ—Ç–≤–µ—Ç
        })
        .catch((error) => {
          if (n === 1) {
            return reject(error); // –ï—Å–ª–∏ –ø–æ–ø—ã—Ç–∫–∏ –∑–∞–∫–æ–Ω—á–∏–ª–∏—Å—å, –æ—Ç–∫–ª–æ–Ω—è–µ–º Promise
          }
          console.log(`Attempt ${attempts - n + 1} failed. Retrying...`);
          attemptFetch(n - 1); // –ü–æ–≤—Ç–æ—Ä—è–µ–º –∑–∞–ø—Ä–æ—Å —Å —É–º–µ–Ω—å—à–µ–Ω–Ω—ã–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ–º –ø–æ–ø—ã—Ç–æ–∫
        });
    };

    attemptFetch(attempts); // –ó–∞–ø—É—Å–∫–∞–µ–º —Å –ø–µ—Ä–≤–æ–π –ø–æ–ø—ã—Ç–∫–∏
  });
}

