  for (let key in obj) {
    if (obj.hasOwnProperty(key)) {
      const value = obj[key];

      // –ï—Å–ª–∏ –∑–Ω–∞—á–µ–Ω–∏–µ ‚Äî –æ–±—ä–µ–∫—Ç, —Ä–µ–∫—É—Ä—Å–∏–≤–Ω–æ –æ—á–∏—â–∞–µ–º –µ–≥–æ
