```class RequestQueue {
    constructor(urls, maxConcurrentRequests) {
        this.urls = urls;
        this.maxConcurrentRequests = maxConcurrentRequests;
        this.queue = [];
        this.activeRequests = 0;
        this.results = [];
    }

    async start() {
        for (let url of this.urls) {
            this.addRequest(() => fetch(url).then(response => response.json()));
        }
    }

    async addRequest(requestFunction) {
        return new Promise((resolve, reject) => {
            this.queue.push({ requestFunction, resolve, reject });
            this.processQueue();
        });
    }

    async processQueue() {
        if (this.activeRequests < this.maxConcurrentRequests && this.queue.length > 0) {
            const { requestFunction, resolve, reject } = this.queue.shift();
            this.activeRequests++;

            try {
                const result = await requestFunction();
                this.results.push(result);
                resolve(result);
            } catch (error) {
                reject(error);
            } finally {
                this.activeRequests--;
                this.processQueue();
            }
        }
    }

    getResults() {
        return this.results;
    }
}```

