```import { FC, useState } from "react";

import styles from "./Tree.module.css";
import { Icon } from "../Icon/Icon";
import type { IconName } from "../Icon/Icon";

type TreeItem = {
  id: number;
  name: string;
  children?: TreeItem[];
};

type TreeProps = {
  items: TreeItem[];
};

export const Tree: FC<TreeProps> = (props) => {
  const [selectedItemId, setSelectedItemId] = useState<number | null>(null);
  const { items } = props;
  return (
    <div className={styles.Tree}>
      <ul>
        {items.map((item: TreeItem) => (
          <TreeItem
            key={item.id}
            item={item}
            selectedItemId={selectedItemId}
            setSelectedItemId={setSelectedItemId}
          />
        ))}
      </ul>
    </div>
  );
};

type TreeItemProps = {
  item: TreeItem;
  selectedItemId: number | null;
  setSelectedItemId: (id: number) => void;
};

const TreeItem: FC<TreeItemProps> = ({
  item,
  selectedItemId,
  setSelectedItemId,
}) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    if (item.children) {
      setIsOpen(!isOpen);
    }
  };

  const handleSelect = () => {
    setSelectedItemId(item.id);
  };

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–∏–ø –∏–∫–æ–Ω–∫–∏
  const getIconName = (name: string, children?: TreeItem[]): IconName => {
    if (children && children.length > 0) {
      return isOpen ? "folder-open" : "folder-close";
    }
    if (name.endsWith(".js")) {
      return "js";
    }
    if (name.endsWith(".tsx")) {
      return "tsx";
    }
    return "file";
  };

  const isSelected = selectedItemId === item.id;

  return (
    <li>
      <div className={`${styles.row} ${isSelected ? styles.selected : ""}`}>
        <span onClick={handleToggle}>
          <Icon name={getIconName(item.name, item.children)} />
        </span>
        <span onClick={handleSelect}>{item.name}</span>
      </div>
      {item.children && isOpen && (
        <ul className={styles.preview}>
          {item.children.map((child) => (
            <TreeItem
              key={child.id}
              item={child}
              selectedItemId={selectedItemId}
              setSelectedItemId={setSelectedItemId}
            />
          ))}
        </ul>
      )}
    </li>
  );
};```

