import { Order } from './Order';

const ordersListClassName = cn('orders-list');

interface OrdersListProps {
  orders: Order[];
  onClick: (orderId: string) => void;
  disabledIds?: string[];
}

const OrdersList: React.FC<OrdersListProps> = ({
  orders,
  onClick,
  disabledIds = [],
}) => {
  return (
    <ul className={ordersListClassName()}>
      {orders.map((order) => {
        const orderClassName = cn('order', { disabled: disabledIds.includes(order.id) });

        return (
          <Order
            key={order.id}
            order={order}
            className={orderClassName}
            onClick={() => onClick(order.id)}
          />
        );
      })}
    </ul>
  );
};

export default OrdersList;```
