async function processArray(numbers, asyncOperation) {
const results = []
for(const number of numbers){
    const result = await asyncOperation(number)
    results.push(result)
}
return results
}

// –ü—Ä–∏–º–µ—Ä –≤—ã–∑–æ–≤–∞ —Ñ—É–Ω–∫—Ü–∏–∏
