import React from "react";
import { ToDo } from "./ToDo";

const ToDoItem = React.memo(({ item, updateHandler, deleteHandler }) => {
  console.log("render ToDoItem", item.id); // –î–æ–±–∞–≤–∏–º item.id –¥–ª—è —è—Å–Ω–æ—Å—Ç–∏
  return (
    <li>
      <input
        id={item.id}
        value={item.text}
        onChange={(e) => updateHandler(item.id, e.target.value)}
      />
      <button onClick={() => deleteHandler(item.id)}>X</button>
    </li>
  );
});

const ToDoList = () => {
  const [todoList, setToDoList] = React.useState([]);

  const addToDo = () => {
    const todoListNext = [...todoList, new ToDo()];
    setToDoList(todoListNext);
  };

  const deleteToDo = (id) => {
    const todoListNext = todoList.filter((i) => i.id !== id);
    setToDoList(todoListNext);
  };

  const updateToDoById = (id, value) => {
    const index = todoList.findIndex((todo) => todo.id === id);
    const listLocal = [...todoList];
    const item = { ...listLocal[index], text: value };
    listLocal[index] = item;

    setToDoList(listLocal);
  };

  console.log("------render ToDoList----------");

  return (
    <div>
      <p>ToDo on hooks</p>
      <ul>
        {todoList.map((l) => (
          <ToDoItem
            key={l.id}
            item={l}
            updateHandler={updateToDoById}
            deleteHandler={deleteToDo}
          />
        ))}
      </ul>
      <button onClick={addToDo}>Add ToDoItem</button>
    </div>
  );
};

export default ToDoList;```

