```function findPairWithSum(target, arr) {
    let mapArr = new Map();
    for (let i = 0; i < arr.length; i++) {
        let num = arr[i];
        let complited = target - num;
        if (mapArr.has(complited)) {
            return [mapArr.get(complited), i]; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∏–Ω–¥–µ–∫—Å—ã
        }
        mapArr.set(num, i);
    }
    return null; // –ï—Å–ª–∏ –ø–∞—Ä–∞ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞
}

