```async function fetchWithRetry(url, retryCount = 3, attempts = 0) {
    try {
        const response = await fetch(url);

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —É—Å–ø–µ—à–µ–Ω –ª–∏ –æ—Ç–≤–µ—Ç
        if (!response.ok) {
            throw new Error(HTTP error! status: ${response.status});
        }

        // –ï—Å–ª–∏ –≤—Å—ë —Ö–æ—Ä–æ—à–æ, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
        return await response.json(); // –∏–ª–∏ response.text() –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ç–∏–ø–∞ –æ—Ç–≤–µ—Ç–∞
    } catch (error) {
        attempts++;
        console.error(Attempt ${attempts} failed: ${error.message});

        // –ï—Å–ª–∏ —ç—Ç–æ –ø–æ—Å–ª–µ–¥–Ω—è—è –ø–æ–ø—ã—Ç–∫–∞, –≤—ã–±—Ä–∞—Å—ã–≤–∞–µ–º –æ—à–∏–±–∫—É
        if (attempts >= retryCount) {
            throw new Error(Failed to fetch after ${retryCount} attempts: ${error.message});
        }

        // –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π –≤—ã–∑–æ–≤ –¥–ª—è —Å–ª–µ–¥—É—é—â–µ–π –ø–æ–ø—ã—Ç–∫–∏
        return fetchWithRetry(url, retryCount, attempts);
    }
}

