interface Post {
    id: number;
    userId: number;
    title: string;
    body: string;
}

type Comment = {
    id: number;
    postId: number;
    name: string;
    email: string;
    body: string;
};

const LargeList = ({ commentPrefix, error = false }: {commentPrefix: string, error?: boolean}) => {
    const [posts, setPosts] = useState([]);
    const [comments, setComments] = useState([]);

    /* fetch data */
    // @ts-ignore
    useEffect(() => {
        const fetchData = async () => {
            try {
                const postsResponse = await fetch(
                    'https://jsonplaceholder.typicode.com/posts'
                );
                const postsJson = await postsResponse.json();
                setPosts(postsJson);
                const commentsResponse = await fetch(
                    'https://jsonplaceholder.typicode.com/comments'
                );
                const commentsJson = await commentsResponse.json();
                setComments(commentsJson);
            } catch(e) {
                console.error(e);
            }
        }
        fetchData();
    }, []);

    /* get all comments by postId */
    const findRelatedComments = (postId: number) => {
        return comments.filter(comment => comment.postId === postId);
    };

    if (error) {
        return;
    }

    return (
        <div>
            {/* render posts */}
            {posts.map(({ title, body, id }) => (
                <main className="post" key={id}>
                    <h3 className="post-Item_red">{title}</h3>
                    <p>{body}</p>
                    <hr /> {/* css */}
                    {/* render comments list */}
                    <ul className="postList">
                        {(findRelatedComments(id) as Comment[]).map(
                            ({ body, id }) => (
                                <li className="post__header" key={id}>
                                    {commentPrefix} {body}
                                </li>
                            )
                        )}
                    </ul>
                </main>
            ))}
        </div>
    );
};

