```import React from "react";

export function App() {
  const [numOfRequests, setNumOfRequests] = React.useState(10);
  const [responseTimes, setResponseTimes] = React.useState([]);
  const [count, setCount] = React.useState(0);
  const [url] = React.useState("https://codesandbox.io");

  const handleClick = async () => {
    try {
      let times = [];
      for (let i = 1; i <= numOfRequests; i++) {
        let startTime = new Date(); // –ó–∞–ø–æ–º–∏–Ω–∞–µ–º –≤—Ä–µ–º—è –Ω–∞—á–∞–ª–∞ –∑–∞–ø—Ä–æ—Å–∞
        await fetch(url);
        let endTime = new Date(); // –ó–∞–ø–æ–º–∏–Ω–∞–µ–º –≤—Ä–µ–º—è –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –∑–∞–ø—Ä–æ—Å–∞
        let elapsedTime = endTime - startTime; // –í—ã—á–∏—Å–ª—è–µ–º –≤—Ä–µ–º—è, –∑–∞—Ç—Ä–∞—á–µ–Ω–Ω–æ–µ –Ω–∞ –∑–∞–ø—Ä–æ—Å
        times.push(elapsedTime); // –î–æ–±–∞–≤–ª—è–µ–º –≤—Ä–µ–º—è –≤ –º–∞—Å—Å–∏–≤
        setCount(i); // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Å–æ–≤–µ—Ä—à–µ–Ω–Ω—ã—Ö –∑–∞–ø—Ä–æ—Å–æ–≤
      }
      setResponseTimes(times); // –°–æ—Ö—Ä–∞–Ω—è–µ–º –º–∞—Å—Å–∏–≤ –≤—Ä–µ–º–µ–Ω –æ—Ç–≤–µ—Ç–æ–≤
    } catch (error) {
      console.error(error);
    }
  };

  const calculateAverage = () => {
    if (responseTimes.length === 0) {
      return 0;
    }
    const sum = responseTimes.reduce((acc, time) => acc + time, 0);
    return sum / responseTimes.length;
  };

  return (
    <div>
      <div>
        <label>URL</label>
        <input value={url} readOnly />
      </div>
      <div>
        <label>Number of requests</label>
        <input
          type="number"
          value={numOfRequests}
          onChange={(e) => setNumOfRequests(parseInt(e.target.value))}
        />
      </div>
      <div>
        <label>Avg response time</label>
        <input value={calculateAverage()} readOnly />
      </div>
      <div>
        <label>Requests made</label>
        <input value={count} readOnly />
      </div>
      <button onClick={handleClick}>Start</button>
    </div>
  );
}```

