const tree = {
  value: 1,
  children: [
    { value: 2, children: [{ value: 3 }] },
    { value: 4, children: [{ value: 5 }, { value: 6 }] },
  ],
};

const getTreeValues = (tree) => {
  const stack = [tree];
  const result = [];

  while (stack.length > 0) {
    const node = stack.pop();
    result.push(node.value);

    if (node.children?.length > 0) {
      node.children.forEach((child) => {
        stack.push(child);
      });
    }
  }

  return result;
};```

