```// getRanges([1,2,5,10,6,7,8,11,13,0]) => '0-2,5-8,10-11,13' 
function getRanges(arr) {
    if (arr.length === 0) return "";

    arr.sort((a, b) => a - b); // –°–æ—Ä—Ç–∏—Ä—É–µ–º –º–∞—Å—Å–∏–≤

    let ranges = [];
    let start = arr[0];
    let end = arr[0];

    for (let i = 1; i < arr.length; i++) {
        if (arr[i] === end + 1) {
            // –ï—Å–ª–∏ —Ç–µ–∫—É—â–µ–µ —á–∏—Å–ª–æ —è–≤–ª—è–µ—Ç—Å—è –ø—Ä–æ–¥–æ–ª–∂–µ–Ω–∏–µ–º –¥–∏–∞–ø–∞–∑–æ–Ω–∞
            end = arr[i];
        } else {
            // –ï—Å–ª–∏ —Ç–µ–∫—É—â–µ–µ —á–∏—Å–ª–æ –Ω–µ —è–≤–ª—è–µ—Ç—Å—è –ø—Ä–æ–¥–æ–ª–∂–µ–Ω–∏–µ–º –¥–∏–∞–ø–∞–∑–æ–Ω–∞, —Å–æ—Ö—Ä–∞–Ω—è–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–π –¥–∏–∞–ø–∞–∑–æ–Ω
            ranges.push(start === end ? `${start}` : `${start}-${end}`);
            start = arr[i];
            end = arr[i];
        }
    }

    // –î–æ–±–∞–≤–ª—è–µ–º –ø–æ—Å–ª–µ–¥–Ω–∏–π –¥–∏–∞–ø–∞–∑–æ–Ω
    ranges.push(start === end ? `${start}` : `${start}-${end}`);

    return ranges.join(',');
}```
