  const [state, setState] = useState(() => incrementor());

  useEffect(() => {
    const intervalID = setInterval(() => setState(incrementor()), 1000);
    return () => {
      clearInterval(intervalID);
    };
  }, []);

  return <div>{state}</div>;
};

