function solution(connect, render) {
    let messages = new Map();
    let lastRenderedId = 0;

    connect((msg) => {
        messages.set(msg.id, msg);

        while (messages.has(lastRenderedId + 1)) {
            lastRenderedId++;
            render(messages.get(lastRenderedId));
            messages.delete(lastRenderedId); // –£–¥–∞–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –ø–æ—Å–ª–µ –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è
        }

