        if (messages.size > 1000) {
            console.warn("–ë–æ–ª–µ–µ 1000 —Å–æ–æ–±—â–µ–Ω–∏–π –æ–∂–∏–¥–∞—é—Ç –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è");
        }
    });
}
