function myPromiseAll(promises) {
  return new Promise((resolve, reject) => {

    const results = [];
    let completedPromises = 0;

    promises.forEach((promise, index) => {
      promise.then((value) => {
          results[index] = value;
          completedPromises += 1;

          if (completedPromises === promises.length) {
            resolve(results);
          }
        })
        .catch((error) => {
          reject(error);
        });
    });
  });
}
