```function countConsecutiveChars(inputString) {
    if (inputString.length === 0) {
        return ''; // –ï—Å–ª–∏ —Å—Ç—Ä–æ–∫–∞ –ø—É—Å—Ç–∞—è, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ø—É—Å—Ç—É—é —Å—Ç—Ä–æ–∫—É
    }

    let result = '';
    let count = 1; // –°—á—ë—Ç—á–∏–∫ –ø–æ–≤—Ç–æ—Ä–µ–Ω–∏–π, –Ω–∞—á–∏–Ω–∞–µ–º —Å 1, —Ç–∞–∫ –∫–∞–∫ –ø–µ—Ä–≤—ã–π —Å–∏–º–≤–æ–ª –≤—Å–µ–≥–¥–∞ —Å—á–∏—Ç–∞–µ—Ç—Å—è

    for (let i = 0; i < inputString.length; i++) {
        if (inputString[i] === inputString[i + 1]) {
            count++; // –ï—Å–ª–∏ —Ç–µ–∫—É—â–∏–π —Å–∏–º–≤–æ–ª —Ä–∞–≤–µ–Ω —Å–ª–µ–¥—É—é—â–µ–º—É, —É–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å—á—ë—Ç—á–∏–∫
        } else {
            // –ï—Å–ª–∏ —Ç–µ–∫—É—â–∏–π —Å–∏–º–≤–æ–ª –æ—Ç–ª–∏—á–∞–µ—Ç—Å—è –æ—Ç —Å–ª–µ–¥—É—é—â–µ–≥–æ, –¥–æ–±–∞–≤–ª—è–µ–º –µ–≥–æ –∏ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –ø–æ–≤—Ç–æ—Ä–µ–Ω–∏–π –∫ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—É
            result += inputString[i];
            if (count > 1) {
                result += count;
            }
            count = 1; // –°–±—Ä–∞—Å—ã–≤–∞–µ–º —Å—á—ë—Ç—á–∏–∫ –¥–ª—è –Ω–æ–≤–æ–π –≥—Ä—É–ø–ø—ã —Å–∏–º–≤–æ–ª–æ–≤
        }
    }

    return result;
}

// –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è:
