// –î–æ–±–∞–≤–ª—è–µ–º –º–µ—Ç–æ–¥ getValues –∫ –æ–±—ä–µ–∫—Ç—É
Object.prototype.getValues = function(...keys) {
    // –ï—Å–ª–∏ –∞—Ä–≥—É–º–µ–Ω—Ç –æ–¥–∏–Ω –∏ —ç—Ç–æ —Å—Ç—Ä–æ–∫–∞, —Ä–∞–∑–±–∏–≤–∞–µ–º –µ—ë –Ω–∞ –º–∞—Å—Å–∏–≤
    if (keys.length === 1 && typeof keys[0] === 'string') {
