```const goods = [ 
  {id: 'ab', name: '–ò–º—è-01', type: '—Å—Ç–∞–ª—å', weight: 1 }, 
  {id: 'bc',name: '–ò–º—è-02', type: '—á—É–≥—É–Ω', weight: 2 }, 
  {id: 'cd',name: '–ò–º—è-03', type: 'c—Ça–ª—å', weight: 3 }, 
  {id: 'de',name: '–ò–º—è-04', type: '—á—É–≥—É–Ω', weight: 4 }, 
  {id: 'ef',name: '–ò–º—è-05', type: '—á—É–≥—É–Ω', weight: 5 }, 
  {id: 'fg',name: '–ò–º—è-03', type: 'c—Ça–ª—å', weight: '4'}, 
];

// –ó–∞–¥–∞—á–∞: –ø–æ–ª—É—á–∏—Ç—å —Ç–∞–±–ª–∏—Ü—É –ø–æ —Ä–æ–ª—è–º + totalWeight:
// {
//    '—Å—Ç–∞–ª—å': {ids: ['ab', 'cd', 'fg'], totalWeight: 8},
//    '—á—É–≥—É–Ω': {ids: ['bc', 'de', 'ef'], totalWeight: 11},
// }

// —Ä–µ—à–µ–Ω–∏–µ
const result = goods.reduce((acc, item) => {
  // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º weight –≤ —á–∏—Å–ª–æ
  const weight = +item.weight;

  if (!acc[item.type]) {
    acc[item.type] = { ids: [], totalWeight: 0 };
  }

  acc[item.type].ids.push(item.id);
  acc[item.type].totalWeight += weight;

  return acc;
}, {});
```

