function sortEvensOnly(arr = []) {
  // 1
  let evenIndexes = [];
  let evens = [];
  arr.forEach((num, idx) => {
    if (num % 2 === 0) {
      evenIndexes.push(idx);
      evens.push(num);
    }
  });

  // 2
  evens.sort((a, b) => a - b);

  // 3
  evenIndexes.forEach((index, i) => {
    arr[index] = evens[i];
  });

  return arr;
}

