function newPromiseAll(promises) {

    if (promises.length === 0){
    return Promise.resolve([])
  }

  return new Promise((resolve, reject) => {
    let result = [];
    let allResolved = 0;
    promises.forEach((curr, i) => {
      Promise.resolve(curr).then((data) => {
        result[i] = data;
        allResolved += 1;
        if (allResolved === promises.length){
          resolve(result)
        }
      }).catch((error) => reject(error))
    })
  })```

