class List<T> {

    private #items:T[] = []
    private getItemKey: (item:T) => string

    constructor({getItemKey}:{ getItemKey: (item:T) => string}){
        this.getItemKey = getItemKey
    }

    addItem(item:T){
        this.#items.push(item)
    }

    removeItem(item:T){
        const key = this.getItemKey(item)
        this.#items = this.#items.filter(currentItem => this.getItemKey(currentItem) !== key)
    }

    get items():ReadonlyArray<T>{
        return this.#items
    }
}

