function myPromiseAll(promises){
    return new Promise((resolve,reject) => {
        const result = []
        let count = 0
        promises.forEach((promise,index)=> {
            Promise.rosolve(promise).then(value => {
                result[index] = value
                count++

                if(promises.length === /* result.length */ count){
                    resolve(result)
                }
            })
            .catch(reject)
        })
        if(promises.length === 0){
            resolve(result)
        }
    })
}

